<script src="https://code.jquery.com/jquery-3.6.0.min.js"></script>
<script>
    $(document).ready(function() {
        // Show profile card on button click
        $('#showProfile').click(function() {
            // Fetch profile data via AJAX
            $.ajax({
                url: 'profile.php',
                type: 'GET',
                dataType: 'json',
                success: function(data) {
                    if (data.error) {
                        alert(data.error);
                    } else {
                        const roleMapping = {
                            TD: 'Teacher Degree',
                            TJ: 'Teacher Junior',
                            OO: 'Office Operator',
                            NL: 'Non Teaching Lab',
                            NO: 'Non Teaching Office'
                        };
                        const roleDescription = roleMapping[data.Role] || 'Unknown Role';
                        // Populate profile data in the card
                        $('#name').text(data.Name);
                        $('#username').text(data.Username);
                        $('#department').text(data.Department_name);
                        $('#role').text(roleDescription);
                        $('#dateOfJoining').text(data.Date_of_Joining);
                        $('#Designation').text(data.Designation);
                        $('#profileCard').removeClass('hidden'); // Show the card
                    }
                },
                error: function() {
                    alert('Error fetching profile data.');
                }
            });
        });

        // Hide profile card on close button click
        $('#closeCard').click(function() {
            $('#profileCard').addClass('hidden'); // Hide the card
        });
    });
</script>


<script>
    // JavaScript to toggle the profile dropdown menu
    const profileButton = document.getElementById('profileButton');
    const profileMenu = document.getElementById('profileMenu');

    // Toggle the menu visibility on button click
    profileButton.addEventListener('click', (event) => {
        event.stopPropagation(); // Prevent the click from propagating to the document
        profileMenu.classList.toggle('hidden');
    });

    // Hide the menu when clicking outside of it
    document.addEventListener('click', (event) => {
        if (!profileMenu.contains(event.target) && !profileButton.contains(event.target)) {
            profileMenu.classList.add('hidden');
        }
    });
</script>